import useForm from '../lib/useForm';

function CreateProduct() {
  const { inputs, handleChange, clearForm, formReset } = useForm({
    name: 'Nice Shoes',
    price: 34234,
    description: 'These are nice shoes',
  });

  return (
    <form>
      <label htmlFor="name">
        Name
        <input
          type="text"
          id="name"
          name="name"
          value={inputs.name}
          onChange={handleChange}
          placeholder="Name"
        />
      </label>
      <label htmlFor="price">
        Price
        <input
          type="number"
          id="price"
          name="price"
          value={inputs.price}
          onChange={handleChange}
          placeholder="Price"
        />
      </label>
      <button onClick={clearForm}>Clear Form</button>
    </form>
  );
}

export default CreateProduct;
