import { PAGINATION_QUERY } from '../components/Pagination';

export default function paginationField() {
  return {
    keyArgs: false, // Tels apollo we will take care of everything
    read(existing = [], { args, cache }) {
      console.log({ existing, args, cache });
      const { skip, first } = args;

      // Read the number of items on the page from the cache
      const data = cache.readQuery({ query: PAGINATION_QUERY });
      console.log(data);

      const count = data?._allProductsMeta?.count;
      const page = skip / first + 1;
      const pages = Math.ceil(count / first);

      // Check if we have existing items
      const items = existing.slice(skip, skip + first).filter((x) => x);

      if (items.length !== first) {
        // We don't have any items we must go to the network to fetch them
        return false;
      }

      // If ther are items just return them from the cache
      if (items.length) {
        console.log(
          `There are ${items.length} items in the cache and we will send them to Apollo`
        );

        return items;
      }

      return false; // fallback to network
      // First thing it does is it asks the read function for those items
      // We can either do one of 2 things:
      // First thing we can do is return the items because they are already in the cache
      // The other thing we can do is to return false from here and send a network request
    },
    merge() {
      // this runs when the apollo client comes back from the network with our products
    },
  };
}
