export default function formatMoney(amount = 0) {
  const options = {
    style: 'currency',
    currency: 'ZAR',
    minimumFractionDigits: 2,
  };

  const formatter = new Intl.NumberFormat('en-ZA', options);

  return formatter.format(amount / 100);
}
